import React from "react"

//Importing screen nested in calculator
import Screen from "./Screen/Screen.js"
import Keypad from "./Keypad/Keypad.js"



class Calculator extends React.Component {
  state = {
    equation: '',
    result: 0
  }

  //Calculator Logic:
  onButtonPress = event => {
    let equation = this.props.equation;
    const pressedButton = event.target.innerHTML;

    if(pressedButton = 'C') return this.clear();
    //building the number:
    if(pressedButton >= '0' && pressedButton <= '9' || pressedButton === '.') equation+=pressedButton;

  }

  render() {
    return(
      <main className="calculator">
        <Screen equation={this.state.equation} result={this.state.result}/>
        <Keypad/>
      </main>
    )
  }
}

export default Calculator
